function xqLoading(a,n){let d=document.createElement("div");d.className="loading";let e='<div class="loading-inner"><span class="loading-circle" style="border-left-color: '+n+';"></span><img class="loading-img" src="'+a+'"</div>';d.innerHTML=e,document.body.appendChild(d)}let xqloading=document.getElementById("xqloading"),params=xqloading.dataset;xqLoading(params.img,params.color),window.onload=function(){document.querySelector(".loading").classList.add("active")};